package com.ds.arrays;

public class OneDArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] numbers = new int[5];
		numbers[0]= 20;
		numbers[1]= 5;
		numbers[2]= 3;
		numbers[3]= 10;
		numbers[4]= 15;
		for (int i=0; i<numbers.length; i++)
			System.out.println(numbers[i]);
	}

}
